import React, { useState } from "react";
//import {auth} from '../database/firebase'
import {Button} from 'react-native-elements'
import * as Google from 'expo-google-app-auth';

import firebase from 'firebase/app';
import 'firebase/auth';

import {
  View,
  StyleSheet,
  TextInput,
  ScrollView,
} from "react-native";


const LoginScreen = (props) => {

  const [state, setState] = useState
    ({
        email: '',
        pass: '',
    });
    var emailw = state.email
    var passw = state.pass
    const auth = firebase.auth();

    const login = async () =>
    {
        try {
            auth.signInWithEmailAndPassword(emailw,passw).then((res) => alert('Usuario logueado'))
             props.navigation.navigate('UsersList')
        } catch (error) {
          console.log('error')
        }
    };
    const signUp = () =>
    {
       try {
            console.log(emailw, passw)
            auth.createUserWithEmailAndPassword(emailw,passw).then((res) => alert('Usuario registrado')) 
            props.navigation.navigate('UsersList')
        } catch (error) {
           alert('Datos Incorrectos');
        }
        
    };

    const googleAuth = () =>
    {
      try {
        const result =  Google.logInAsync({
          androidClientId: '376997193593-5ifpk0ntnlkmd79j42dgnbd0p5tt702f.apps.googleusercontent.com',
          scopes: ['profile', 'email'],
        });
    
        if (result.type === 'success') {
          return result.accessToken;
        } else {
          return { cancelled: true };
        }
      } catch (e) {
        return { error: true };
      }
        /*const provider = new firebase.auth.GoogleAuthProvider();
        auth.signInWithPopup(provider).then(res=>{console.log('google')})*/
        
    }
    return (
    <ScrollView style={styles.container}>
            {/* Email Input */}
            <View style={styles.inputGroup}>
                <TextInput
                placeholder="Email"
                onChangeText={(value) => setState({...state,email: value})}
                value={state.email}
                
                />
            </View>
            {/* Pass Input */}
            <View style={styles.inputGroup}>
                <TextInput
                secureTextEntry={true}
                placeholder="Contraseña"
                onChangeText={(value) => setState({...state,pass: value})}
                value={state.pass}
                />
            </View>
            <View style={styles.inputGroup}>
                <View style={styles.button}>
                    <Button type="outline" title="Login" onPress={() => login()} />
                </View>
            
                <View style={styles.button}>
                    <Button type="outline" title="Sign Up" onPress={() => signUp()} />
                </View>
            </View>

            <View style={styles.buttonG}>
                <Button type="solid" title="Google" onPress={() => googleAuth()} />
            </View>
            
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 35
  },
  inputGroup: {
    flex: 1,
    padding: 0,
    marginBottom: 15,
    borderBottomWidth: 1,
    borderBottomColor: "#cccccc",
  },
  loader: {
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    position: "absolute",
    alignItems: "center",
    justifyContent: "center",
  },
  button: {
    flex: 1,
    padding: 4,
  },
  buttonG: {
    flex: 1,
    padding: 4,
    paddingTop: 15,
  }
});

export default LoginScreen;
