import React, { useState } from "react";
//import {auth} from '../database/firebase'
import {Button} from 'react-native-elements'
import * as Google from 'expo-google-app-auth';

import firebase from 'firebase/app';
import 'firebase/auth';

import {
  View,
  StyleSheet,
  TextInput,
  ScrollView,
} from "react-native";
import { ActivityIndicatorComponent } from "react-native";
import { event } from "react-native-reanimated";


const LoginScreen = (props) => {

  const [state, setState] = useState
    ({
        email: '',
        pass: '',
    });
    var emailw = state.email
    var passw = state.pass
    const auth = firebase.auth();

    const login = async () =>
    {
        
      try {
        auth.signInWithEmailAndPassword(emailw,passw)
      } catch (error) {
        alert('Credenciales incorrectas'+ error)
      }

      /*auth.onAuthStateChanged(user =>{
        if(user != null)
        {
          props.navigation.navigate('UsersList')
        }
        else{
          //alert('Credenciales incorrectas')
        }
      })  */
          
    };
    const signUp = () =>
    {
       try {
            console.log(emailw, passw)
            auth.createUserWithEmailAndPassword(emailw,passw) 
            if(user){
              props.navigation.navigate('UsersList')
            }
            else{
              props.navigation.navigate('login')
            }
        } catch (error) {
           alert('Datos Incorrectos');
        }
        
        
    };

    const googleAuth = () =>
    {
     auth.signOut();
     props.navigation.navigate('login');
    }
    return (
    <ScrollView style={styles.container}>
            {/* Email Input */}
            <View style={styles.inputGroup}>
                <TextInput
                placeholder="Email"
                onChangeText={(value) => setState({...state,email: value})}
                value={state.email}
                
                />
            </View>
            {/* Pass Input */}
            <View style={styles.inputGroup}>
                <TextInput
                secureTextEntry={true}
                placeholder="Contraseña"
                onChangeText={(value) => setState({...state,pass: value})}
                value={state.pass}
                />
            </View>
            <View style={styles.inputGroup}>
                <View style={styles.button}>
                    <Button type="outline" title="Login" onPress={() => login()} />
                </View>
            
                <View style={styles.button}>
                    <Button type="outline" title="Sign Up" onPress={() => signUp()} />
                </View>
            </View>

            <View style={styles.buttonG}>
                <Button type="solid" title="LogOut" onPress={() => googleAuth()} />
            </View>
            
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 35
  },
  inputGroup: {
    flex: 1,
    padding: 0,
    marginBottom: 15,
    borderBottomWidth: 1,
    borderBottomColor: "#cccccc",
  },
  loader: {
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    position: "absolute",
    alignItems: "center",
    justifyContent: "center",
  },
  button: {
    flex: 1,
    padding: 4,
  },
  buttonG: {
    flex: 1,
    padding: 4,
    paddingTop: 15,
  }
});

export default LoginScreen;
